mapper.select:
select id, ep_uid, device_id, type, time, which_round_same, which_round_diff
from bind_history
where id = #{id}

select id, ep_uid, device_id, type, time, which_round_same, which_round_diff
from bind_history


		and ep_uid = #{ep_uid}


		and device_id = #{device_id}


		and type = #{type}


		and time = #{time}


		and which_round_same = #{which_round_same}


		and which_round_diff = #{which_round_diff}

select id, ep_uid, device_id, type, time, which_round_same, which_round_diff
from bind_history

	time is not null
	and device_id is not null

		and ep_uid = #{ep_uid}


		and device_id = #{device_id}


		and type = #{type}


		and time = #{time}


		and which_round_same = #{which_round_same}


		and which_round_diff = #{which_round_diff}


order by time

select id, ep_uid, device_id, type, time, which_round_same, which_round_diff
from bind_history

	time is not null
	and device_id is not null
	and time >= #{start}
	and time <= date_add(#{start}, interval #{range} day)

		and ep_uid = #{0.ep_uid}


		and device_id = #{device_id}


		and type = #{type}


		and time = #{time}


		and which_round_same = #{which_round_same}


		and which_round_diff = #{which_round_diff}


order by time

select id, ep_uid, device_id, type, time, which_round_same, which_round_diff from bind_history
time is not null and device_id is not null and time >= #{start} and time <= date_add(#{start}, interval #{range} day) and ep_uid = #{bh.ep_uid}
order by time

select * from bind_history
where ep_uid = #{bh.ep_uid}
	and device_id = #{did}
