mapper.select:
select 
from ep_attendance
where
ep_uid = #{uid}
and time <= now()
and datediff(now(), time) < #{day_range}

select epu.name,epa.type, epa.latitude, epa.longtitude, epa.wifi_name, epa.time, epa.platform,epu.email from ep_attendance epa,ep_user epu where time between #{startDate} and #{endDate} and epu.email in
#{item}
and epa.ep_uid = epu.id and is_valid = true

SELECT ep_uid, DATE_FORMAT(TIME,'%Y-%m-%d') punch_date, MIN(TIME) first_punch_time, MAX(TIME) last_punch_time
FROM ep_attendance WHERE is_valid = TRUE and mach_sn = '-2' AND TIME IS NOT NULL AND TIME < DATE_FORMAT(NOW(),'%Y-%m-%d') and ( 1 > 2
or DATE_FORMAT(TIME,'%Y-%m-%d') in (
DATE_FORMAT( #{item}, '%Y-%m-%d' )
)
) and ( 1 > 2
or ep_uid in (
#{item.id}
)
) GROUP BY ep_uid, punch_date

SELECT ep_uid, DATE_FORMAT(TIME,'%Y-%m-%d') punch_date, MIN(TIME) first_punch_time, MAX(TIME) last_punch_time
	
FROM ep_attendance
WHERE is_valid = TRUE
	and mach_sn = '-2'	
	AND TIME IS NOT NULL
	AND TIME < DATE_FORMAT(NOW(),'%Y-%m-%d')
	
		and DATE_FORMAT(TIME,'%Y-%m-%d') in (
			
				#{item}
			
		)
	
	
		and ep_uid in (
			
				
					#{item.id}
				
			
		)
	
GROUP BY ep_uid, punch_date

mapper.update:
update ep_attendance
proc_result = #{procResult},
proc_count = (ifnull(proc_count, 0) + 1), proc_time = now()
where 1 < 1
or ( DATE_FORMAT(TIME,'%Y-%m-%d') in (
DATE_FORMAT( #{date}, '%Y-%m-%d' )
)
and ep_uid in (
#{epuid}
)
and 1 = 2
)
