mapper.insert:
insert into ep_dept(name, level, superior_id, `order`)
value (
	#{name}, #{level}, #{superior_id}, #{order}
)

mapper.select:
select id, name, level, superior_id, `order`
from ep_dept
where 1=1

		and id = #{id}


		and name = #{name}


		and level = #{level}


		and superior_id = #{superior_id}


		and `order` = #{order}
